const thin_copy = (h_src) => {
	// create new hash
	let h_dst = Object.create(h_src);

	// src has weak descendents
	h_src[$_KIDS] = 1;

	// dst has weak keys
	h_dst[$_THIN] = 1;

	return h_dst;
};

