					// no quads; empty tree
					if(!c_graphs) return this.offspring();

					// save graph-keys and quads count
					h_quads_dst[$_KEYS] = c_graphs;
					h_quads_dst[$_QUADS] = c_quads;

					// save graph-keys and quads counts
					return this.offspring(h_quads_dst);
